const CannedResponse = ({ cannedResponse }: { cannedResponse: { id: string; value: string } }) => {
  const [id, value] = cannedResponse?.value || ['', '']
  return (
    <div className='group group relative flex min-h-[40px] justify-between bg-white hover:bg-[#FAFAFA]'>
      <div className='group flex w-full items-start gap-[17px] py-[8px] ps-[15px] pe-[38px] font-light text-[#656565] [word-break:break-word] [&:first-child]:rounded-t-[3px]'>
        <img src='icons/puzzle.svg' alt='' className='mt-[4px] w-[16px] min-w-[16px]' />
        <div className={twMerge('invisible', value && 'visible')}>{value || 'loading'}</div>
      </div>
      <Tooltip value='Copy' side='top'>
        <div
          onClick={(e) => copy(id || '', e.currentTarget)}
          className='absolute top-[8px] right-[10px] hidden size-[28px] cursor-pointer items-center justify-center rounded-[6px] border border-[#EEEEEE] bg-white hover:border-[#E9EBEF] hover:bg-[#F3F5F9] group-hover:flex'
        >
          <img src='icons/copy.svg' alt='' />
        </div>
      </Tooltip>
    </div>
    // <Tooltip value={<TooltipComponent fragmentId={fragment.id} />} side='top' align='start' className='ml-[23px] -mb-[10px] font-medium font-inter'>
    // </Tooltip>
  )
}

export default CannedResponse
