import { type ClassNameValue, twMerge } from 'tailwind-merge'

interface Props {
  title: string
  subTitle?: string
  className?: ClassNameValue
  wrapperClassName?: ClassNameValue
  imgClassName?: ClassNameValue
  imgUrl?: string
}

const EmptyState = ({
  title,
  subTitle,
  wrapperClassName,
  className,
  imgClassName,
  imgUrl,
}: Props) => {
  return (
    <div
      className={twMerge(
        'm-auto flex h-full w-full flex-col items-center justify-center',
        wrapperClassName
      )}
    >
      <div
        className={twMerge(
          '-translate-y-[70px] flex flex-col items-center justify-center',
          className
        )}
      >
        <img
          className={twMerge('pointer-events-none size-[330px] rounded-full', imgClassName)}
          src={imgUrl || 'empty-state.svg'}
          alt=''
        />
        <h2 className='mt-[30px] font-inter font-normal text-[#656565] text-[18px]'>{title}</h2>
        {subTitle && (
          <p className='mt-[10px] max-w-[378px] text-center font-inter font-normal text-[#656565] text-[15px]'>
            {subTitle}
          </p>
        )}
      </div>
    </div>
  )
}

export default EmptyState
